describe("cache.file", function()
	local meta, is, cache
	setup(function()
    meta = require "meta"
    is = meta.is ^ 'testdata'
    cache = meta.cache
	end)
  it("meta", function()
    assert.truthy(is)
    assert.truthy(is.callable(cache.file))
  end)
  it("positive", function()
    assert.ends('meta/init.lua', cache.file('meta'))
    assert.ends('testdata/loader/init.lua', cache.file('testdata.loader'))
    assert.ends('testdata/loader/init.lua', cache.file('testdata/loader'))
    assert.ends('testdata/loader/init.lua', cache.file('testdata', 'loader'))
    assert.ends('testdata/loader/noinit/message.lua', cache.file('testdata.loader.noinit', 'message'))
    assert.ends('testdata/loader/noinit/message.lua', cache.file('testdata/loader/noinit', 'message'))
    assert.ends('testdata/loader/noinit/message.lua', cache.file('testdata.loader.noinit.message'))
    assert.ends('testdata/loader/noinit/message.lua', cache.file('testdata/loader/noinit/message'))
    assert.ends('testdata/loader/noinit/message.lua', cache.file('testdata.loader.noinit', 'message'))
    assert.ends('testdata/loader/noinit/message.lua', cache.file('testdata/loader/noinit', 'message'))
    assert.ends('testdata/loader/noinit/message.lua', cache.file('testdata.loader.noinit.message'))
    assert.ends('testdata/loader/noinit/message.lua', cache.file('testdata/loader/noinit/message'))
  end)
  it("negative", function()
    assert.is_nil(cache.file('testdata.loader', 'noinit'))
    assert.is_nil(cache.file('testdata/loader', 'noinit'))

    assert.is_nil(cache.file[''])
    assert.is_nil(cache.file[{}])
    assert.is_nil(cache.file[0])
    assert.is_nil(cache.file[false])
    assert.is_nil(cache.file[true])

    assert.is_nil(cache.file(''))
    assert.is_nil(cache.file({}))
    assert.is_nil(cache.file(0))
    assert.is_nil(cache.file(false))
    assert.is_nil(cache.file(true))
  end)
  it("nil", function()
    assert.is_nil(cache.file())
    assert.is_nil(cache.file(nil))
    assert.is_nil(cache.file(nil, nil))
  end)
end)